#
# TABLE STRUCTURE FOR: attendance
#

DROP TABLE IF EXISTS `attendance`;

CREATE TABLE `attendance` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `date` date NOT NULL,
  `shift_id` int(11) DEFAULT NULL,
  `clock_in` time DEFAULT NULL,
  `clock_out` time DEFAULT NULL,
  `status` enum('present','late','absent','excused','holiday','paid_leave','unpaid_leave','annual_leave','excused_late','sick_leave','weekly_leave') DEFAULT NULL,
  `is_late` tinyint(1) DEFAULT 0,
  `overtime_hours` decimal(5,2) DEFAULT 0.00,
  `note` text DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `shift_id` (`shift_id`),
  CONSTRAINT `attendance_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE,
  CONSTRAINT `attendance_ibfk_2` FOREIGN KEY (`shift_id`) REFERENCES `shifts` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=360 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: customers
#

DROP TABLE IF EXISTS `customers`;

CREATE TABLE `customers` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `access_token` varchar(100) NOT NULL,
  `phone` varchar(20) DEFAULT NULL,
  `email` varchar(100) DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  UNIQUE KEY `access_token` (`access_token`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;

#
# TABLE STRUCTURE FOR: inv_currency_logs
#

DROP TABLE IF EXISTS `inv_currency_logs`;

CREATE TABLE `inv_currency_logs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `currency_code` varchar(10) NOT NULL COMMENT 'USD, EUR, GOLD, vb.',
  `rate` decimal(15,4) NOT NULL COMMENT 'TL Karşılığı',
  `source` varchar(50) DEFAULT 'TCMB',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `idx_currency_date` (`currency_code`,`created_at`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;

#
# TABLE STRUCTURE FOR: inv_entities
#

DROP TABLE IF EXISTS `inv_entities`;

CREATE TABLE `inv_entities` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `type` enum('supplier','customer','both','staff') DEFAULT 'supplier',
  `tax_id` varchar(50) DEFAULT NULL,
  `phone` varchar(20) DEFAULT NULL,
  `email` varchar(100) DEFAULT NULL,
  `address` text DEFAULT NULL,
  `contact_info` text DEFAULT NULL,
  `balance` decimal(15,4) DEFAULT 0.0000,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `is_active` tinyint(1) DEFAULT 1,
  `access_token` varchar(64) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `access_token` (`access_token`)
) ENGINE=InnoDB AUTO_INCREMENT=119 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;



#
# TABLE STRUCTURE FOR: inv_entity_balances
#

DROP TABLE IF EXISTS `inv_entity_balances`;

CREATE TABLE `inv_entity_balances` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `entity_id` int(11) NOT NULL,
  `asset_type` enum('TL','USD','EUR','GOLD','CREDIT_CARD') NOT NULL DEFAULT 'TL',
  `amount` decimal(15,4) DEFAULT 0.0000,
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  PRIMARY KEY (`id`),
  UNIQUE KEY `uq_entity_asset` (`entity_id`,`asset_type`),
  CONSTRAINT `inv_entity_balances_ibfk_1` FOREIGN KEY (`entity_id`) REFERENCES `inv_entities` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;

#
# TABLE STRUCTURE FOR: inv_entity_transactions
#

DROP TABLE IF EXISTS `inv_entity_transactions`;

CREATE TABLE `inv_entity_transactions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `invoice_id` int(11) DEFAULT NULL,
  `entity_id` int(11) NOT NULL,
  `wallet_id` int(11) DEFAULT NULL,
  `linked_transaction_id` int(11) DEFAULT NULL,
  `category_id` int(11) DEFAULT NULL,
  `type` enum('fatura','tahsilat','odeme','expense','income','virman','baslangic_bakiyesi','fis') NOT NULL,
  `amount` decimal(15,4) DEFAULT 0.0000,
  `asset_type` enum('TL','USD','EUR','GOLD','CREDIT_CARD') NOT NULL DEFAULT 'TL',
  `exchange_rate` decimal(15,4) DEFAULT 1.0000,
  `asset_amount` decimal(15,4) DEFAULT NULL,
  `tax_total` decimal(15,4) DEFAULT 0.0000,
  `discount_total` decimal(15,4) DEFAULT 0.0000,
  `description` varchar(500) DEFAULT NULL,
  `transaction_date` date NOT NULL,
  `due_date` date DEFAULT NULL,
  `document_no` varchar(50) DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `installment_count` int(11) DEFAULT 1,
  `installment_no` int(11) DEFAULT 1,
  `parent_transaction_id` int(11) DEFAULT NULL,
  `commission_fee` decimal(15,4) DEFAULT 0.0000 COMMENT 'Banka veya dost komisyonu',
  `tax_rate` decimal(5,2) DEFAULT 0.00,
  `tax_amount` decimal(15,4) DEFAULT 0.0000,
  `discount_amount` decimal(15,4) DEFAULT 0.0000,
  `tax_included` tinyint(1) DEFAULT 0,
  PRIMARY KEY (`id`),
  KEY `idx_entity_date` (`entity_id`,`transaction_date`),
  KEY `fk_trans_wallet` (`wallet_id`),
  CONSTRAINT `fk_trans_wallet` FOREIGN KEY (`wallet_id`) REFERENCES `inv_wallets` (`id`) ON DELETE SET NULL,
  CONSTRAINT `inv_entity_transactions_ibfk_1` FOREIGN KEY (`entity_id`) REFERENCES `inv_entities` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=855 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: inv_expense_categories
#

DROP TABLE IF EXISTS `inv_expense_categories`;

CREATE TABLE `inv_expense_categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `is_active` tinyint(4) DEFAULT 1,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;



#
# TABLE STRUCTURE FOR: inv_invoice_items
#

DROP TABLE IF EXISTS `inv_invoice_items`;

CREATE TABLE `inv_invoice_items` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `invoice_id` int(11) NOT NULL,
  `product_id` int(11) DEFAULT NULL,
  `item_type` varchar(20) NOT NULL DEFAULT 'stok',
  `unit` varchar(20) NOT NULL DEFAULT 'Adet',
  `quantity` decimal(15,4) DEFAULT 0.0000,
  `unit_price` decimal(15,4) DEFAULT 0.0000,
  `discount_rate` decimal(5,2) NOT NULL DEFAULT 0.00,
  `discount_amount` decimal(15,4) DEFAULT 0.0000,
  `tax_rate` decimal(5,2) NOT NULL DEFAULT 0.00,
  `tax_amount` decimal(15,4) DEFAULT 0.0000,
  `total_amount` decimal(15,4) DEFAULT 0.0000,
  `description` text DEFAULT NULL,
  `expense_category_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1535 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: inv_invoices
#

DROP TABLE IF EXISTS `inv_invoices`;

CREATE TABLE `inv_invoices` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `invoice_no` varchar(50) NOT NULL,
  `invoice_date` date NOT NULL,
  `due_date` date DEFAULT NULL,
  `type` varchar(20) NOT NULL,
  `entity_id` int(11) NOT NULL,
  `total_amount` decimal(15,4) DEFAULT 0.0000,
  `tax_amount` decimal(15,4) DEFAULT 0.0000,
  `discount_amount` decimal(15,4) DEFAULT 0.0000,
  `general_discount` decimal(15,4) DEFAULT 0.0000,
  `tax_included` tinyint(1) NOT NULL DEFAULT 0,
  `net_amount` decimal(15,4) DEFAULT 0.0000,
  `payment_status` varchar(20) NOT NULL DEFAULT 'unpaid',
  `payment_type` varchar(20) NOT NULL DEFAULT 'cash_bank',
  `wallet_id` int(11) DEFAULT NULL,
  `transfer_entity_id` int(11) DEFAULT NULL,
  `notes` text DEFAULT NULL,
  `status` varchar(20) NOT NULL DEFAULT 'finalized',
  `created_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=677 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: inv_products
#

DROP TABLE IF EXISTS `inv_products`;

CREATE TABLE `inv_products` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `match_names` text DEFAULT NULL,
  `barcode` varchar(100) DEFAULT NULL,
  `unit` varchar(20) DEFAULT 'Adet',
  `stock_quantity` decimal(15,4) DEFAULT 0.0000,
  `avg_cost` decimal(15,4) DEFAULT 0.0000,
  `last_buy_price` decimal(15,4) DEFAULT 0.0000,
  `satis_fiyat` decimal(15,4) DEFAULT 0.0000,
  `tax_rate` decimal(5,2) DEFAULT 20.00,
  `critical_level` decimal(15,4) DEFAULT 0.0000,
  `gorsel` text DEFAULT NULL,
  `kaynak` varchar(50) DEFAULT 'Manuel',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `is_active` tinyint(1) DEFAULT 1,
  PRIMARY KEY (`id`),
  UNIQUE KEY `barcode` (`barcode`)
) ENGINE=InnoDB AUTO_INCREMENT=465 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;



#
# TABLE STRUCTURE FOR: inv_wallets
#

DROP TABLE IF EXISTS `inv_wallets`;

CREATE TABLE `inv_wallets` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `owner_entity_id` int(11) NOT NULL COMMENT 'Cüzdanın sahibi (İşletme için özel bir cari veya Personel/Arkadaş)',
  `name` varchar(100) NOT NULL COMMENT 'Cüzdan Adı (Örn: İş Bankası Kredi Kartı, Ahmetin Kartı)',
  `description` varchar(255) NOT NULL COMMENT 'Açıklamalar',
  `wallet_type` enum('CASH','CREDIT_CARD','BANK_ACCOUNT','GOLD_ACCOUNT','LOAN','SAFE') DEFAULT 'CASH',
  `asset_type` enum('TL','USD','EUR','GOLD') NOT NULL DEFAULT 'TL',
  `limit_amount` decimal(15,4) DEFAULT 0.0000 COMMENT 'Kredi kartı limiti veya Kredi tutarı',
  `balance` decimal(15,4) DEFAULT 0.0000 COMMENT 'Kasadaki para veya Kartın borcu',
  `is_active` tinyint(1) DEFAULT 1,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `statement_day` tinyint(4) DEFAULT 1,
  `payment_day` tinyint(4) DEFAULT NULL COMMENT 'Son Ödeme/Taksit Günü',
  PRIMARY KEY (`id`),
  KEY `owner_entity_id` (`owner_entity_id`),
  CONSTRAINT `inv_wallets_ibfk_1` FOREIGN KEY (`owner_entity_id`) REFERENCES `inv_entities` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: job_items
#

DROP TABLE IF EXISTS `job_items`;

CREATE TABLE `job_items` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `job_id` int(11) DEFAULT NULL,
  `product_id` int(11) DEFAULT NULL,
  `quantity` decimal(10,2) DEFAULT NULL,
  `unit_price` decimal(10,2) DEFAULT NULL,
  `item_date` date DEFAULT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `is_id` (`job_id`),
  KEY `stok_id` (`product_id`),
  CONSTRAINT `fk_job_items_job` FOREIGN KEY (`job_id`) REFERENCES `jobs` (`id`) ON DELETE CASCADE,
  CONSTRAINT `fk_job_items_product` FOREIGN KEY (`product_id`) REFERENCES `inv_products` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=91 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: jobs
#

DROP TABLE IF EXISTS `jobs`;

CREATE TABLE `jobs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `customer_id` int(11) DEFAULT NULL,
  `customer_name_text` varchar(255) NOT NULL,
  `description` text DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `status` enum('Pending','In Progress','Completed','Cancelled') DEFAULT 'Pending',
  `total_amount` decimal(10,2) DEFAULT 0.00,
  `tax_rate` decimal(5,2) DEFAULT 20.00,
  `invoice_status` enum('Kesilmedi','Kesildi') DEFAULT 'Kesilmedi',
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `job_date` date DEFAULT curdate(),
  `tax_included` tinyint(1) DEFAULT 0,
  PRIMARY KEY (`id`),
  KEY `personel_id` (`user_id`),
  CONSTRAINT `fk_jobs_user` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: migrations
#

DROP TABLE IF EXISTS `migrations`;

CREATE TABLE `migrations` (
  `version` bigint(20) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: settings
#

DROP TABLE IF EXISTS `settings`;

CREATE TABLE `settings` (
  `setting_key` varchar(50) NOT NULL,
  `setting_value` text DEFAULT NULL,
  PRIMARY KEY (`setting_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: shifts
#

DROP TABLE IF EXISTS `shifts`;

CREATE TABLE `shifts` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `start_time` time NOT NULL,
  `end_time` time NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



#
# TABLE STRUCTURE FOR: transactions
#

DROP TABLE IF EXISTS `transactions`;

CREATE TABLE `transactions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `type` enum('payment','advance','expense','salary_accrual') NOT NULL,
  `affects_balance` tinyint(1) DEFAULT 1,
  `amount` decimal(10,2) NOT NULL DEFAULT 0.00,
  `description` text DEFAULT NULL,
  `date` date NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id`),
  KEY `idx_user_date` (`user_id`,`date`),
  CONSTRAINT `transactions_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;



#
# TABLE STRUCTURE FOR: users
#

DROP TABLE IF EXISTS `users`;

CREATE TABLE `users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `entity_id` int(11) DEFAULT NULL,
  `full_name` varchar(100) NOT NULL,
  `username` varchar(50) DEFAULT NULL,
  `password` varchar(255) DEFAULT NULL,
  `role` enum('admin','personel') DEFAULT 'personel',
  `hourly_rate` decimal(10,2) DEFAULT 0.00,
  `created_at` timestamp NOT NULL DEFAULT current_timestamp(),
  `salary_day` tinyint(4) DEFAULT 1,
  `annual_leave_days` tinyint(4) DEFAULT 14,
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`),
  KEY `fk_user_entity` (`entity_id`),
  CONSTRAINT `fk_user_entity` FOREIGN KEY (`entity_id`) REFERENCES `inv_entities` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;



